/*
** ANALISE DE DATAS PARA PAGAMENTOS
*/
ALTER TABLE PAGAMENTOS ADD CAD_DATA_FLUXO              DATA;
ALTER TABLE PAGAMENTOS ADD CAD_SEMANA_FLUXO            INTEIRO;
ALTER TABLE PAGAMENTOS ADD CAD_MES_FLUXO               INTEIRO;
ALTER TABLE PAGAMENTOS ADD CAD_TRIMESTRE_FLUXO         INTEIRO;
