/*
** NOVOS CAMPOS NA CONFIGURAÇÃO DO SISTEMA
*/
ALTER TABLE SISCONFIG ADD NATUOPER_COMPRA STR05;
ALTER TABLE SISCONFIG ADD MENSAGEM_OS STR254;
ALTER TABLE SISCONFIG ADD MENSAGEM_VENDA STR254;
UPDATE SISCONFIG SET MENSAGEM_OS = 'HORÁRIO DE ATENDIMENTO: SEGUNDA A SEXTA DAS 09h AS 18h;  AOS SABADOS DAS 9h AS 13h';
UPDATE SISCONFIG SET MENSAGEM_VENDA = 'HORÁRIO DE ATENDIMENTO: SEGUNDA A SEXTA DAS 09h AS 18h;  AOS SABADOS DAS 9h AS 13h';
ALTER TABLE SISCONFIG ADD OSCOND1 STR254;
ALTER TABLE SISCONFIG ADD OSCOND2 STR254;
UPDATE SISCONFIG SET OSCOND1 = '(digite aqui as condições)';
UPDATE SISCONFIG SET OSCOND2 = '(digite aqui as condições)';
INSERT INTO SCRIPTVER(NUMERO, DATA) VALUES (261, CURRENT_TIMESTAMP);
COMMIT;

/*
** REINICIALIZAÇÃO DA BASE DE DADOS
*/
SET TERM ^ ;
CREATE PROCEDURE REINICIAR (
ROTINA INTEGER)
AS
DECLARE VARIABLE CODCLIENTE1 INTEGER;
DECLARE VARIABLE CODCLIENTE2 INTEGER;
begin
/* DETERMINO A ROTINA */
IF (ROTINA IS NULL) THEN
ROTINA = 0;
/* LIMPAR TUDO */
IF (ROTINA = 0) THEN
BEGIN
/* SEQUENCIA */
DELETE FROM
ITSEQUENCIA;
/* PAGAMENTOS */
UPDATE
PAGAMENTOS
SET PAGO = 0;
DELETE FROM
PAGAMENTOS;
UPDATE
SEQUENCIA
SET
SEQUENCIA = 1
WHERE
TABELA = 'PAGAMENTOS';
/* ESTOQUE */
UPDATE
INDIVIDUAIS
SET
VENDIDO = 'N';
DELETE FROM
INDIVIDUAIS;
DELETE FROM
TEMPITENS;
UPDATE
SEQUENCIA
SET
SEQUENCIA = 1
WHERE
TABELA = 'INDIVIDUAIS';
UPDATE
SEQUENCIA
SET
SEQUENCIA = 1
WHERE
TABELA = 'TEMPITENS';
/* PRODUTOS */
DELETE FROM
VARIACAO_VALOR;
DELETE FROM
MENSAGENS;
DELETE FROM
PRODUTOS;
UPDATE
SEQUENCIA
SET
SEQUENCIA = 10000
WHERE
TABELA = 'PRODUTOS';
/* MOVIMENTOS */
DELETE FROM
OS_VENDER;
DELETE FROM
COMISSOES;
DELETE FROM
MOVIMENTOS;
UPDATE
SEQUENCIA
SET
SEQUENCIA = 10000
WHERE
TABELA = 'MOVIMENTOS';
/* EMPRESAS */
DELETE FROM
SISCONFIG
WHERE
CODIGO > 1;
/* CLIENTES */
SELECT
SI.CODCLIENTE,
SI.CODCLIENTE_PADRAO
FROM
SISCONFIG SI
WHERE
SI.CODIGO = 1
INTO
:CODCLIENTE1,
:CODCLIENTE2;
DELETE FROM
CLIENTES
WHERE
CODIGO NOT IN (:CODCLIENTE1, :CODCLIENTE2);
END
end
^
SET TERM ; ^
SET TERM ^ ;
ALTER PROCEDURE REINICIAR (
ROTINA INTEGER)
AS
DECLARE VARIABLE CODCLIENTE1 INTEGER;
DECLARE VARIABLE CODCLIENTE2 INTEGER;
begin
/* DETERMINO A ROTINA */
IF (ROTINA IS NULL) THEN
ROTINA = 0;
/* LIMPAR TUDO */
IF (ROTINA = 0) THEN
BEGIN
/* SEQUENCIA */
DELETE FROM
ITSEQUENCIA;
/* PAGAMENTOS */
UPDATE
PAGAMENTOS
SET PAGO = 0;
DELETE FROM
COMISSOES;
DELETE FROM
PAGAMENTOS;
UPDATE
SEQUENCIA
SET
SEQUENCIA = 1
WHERE
TABELA = 'PAGAMENTOS';
/* ESTOQUE */
UPDATE
INDIVIDUAIS
SET
VENDIDO = 'N';
DELETE FROM
INDIVIDUAIS;
DELETE FROM
TEMPITENS;
UPDATE
SEQUENCIA
SET
SEQUENCIA = 1
WHERE
TABELA = 'INDIVIDUAIS';
UPDATE
SEQUENCIA
SET
SEQUENCIA = 1
WHERE
TABELA = 'TEMPITENS';
/* PRODUTOS */
DELETE FROM
VARIACAO_VALOR;
DELETE FROM
MENSAGENS;
DELETE FROM
CONTRATOS;
DELETE FROM
PRODUTOS;
UPDATE
SEQUENCIA
SET
SEQUENCIA = 10000
WHERE
TABELA = 'PRODUTOS';
/* MOVIMENTOS */
DELETE FROM
OS_VENDER;
DELETE FROM
MOVIMENTOS;
UPDATE
SEQUENCIA
SET
SEQUENCIA = 10000
WHERE
TABELA = 'MOVIMENTOS';
/* EMPRESAS */
DELETE FROM
TRANSFERENCIAS;
DELETE FROM
SISCONFIG
WHERE
CODIGO > 1;
/* CLIENTES */
SELECT
SI.CODCLIENTE,
SI.CODCLIENTE_PADRAO
FROM
SISCONFIG SI
WHERE
SI.CODIGO = 1
INTO
:CODCLIENTE1,
:CODCLIENTE2;
DELETE FROM
CLIENTES
WHERE
CODIGO NOT IN (:CODCLIENTE1, :CODCLIENTE2);
UPDATE
SEQUENCIA
SET
SEQUENCIA = UDF_MAX(:CODCLIENTE1, :CODCLIENTE2) + 1
WHERE
TABELA = 'CLIENTES';
UPDATE
CLIENTES
SET
NOMEUSER = 'SYSDBA'
WHERE
CODIGO = :CODCLIENTE1;
END
end
^
SET TERM ; ^
GRANT EXECUTE ON PROCEDURE REINICIAR TO PUBLIC;
INSERT INTO SCRIPTVER(NUMERO, DATA) VALUES (262, CURRENT_TIMESTAMP);
COMMIT;

/*
** ANALISE DE FLUXO DE CAIXA
*/
SET TERM ^ ;
CREATE PROCEDURE ANALISE_FLUXOCAIXA_DIA (
DATAINI DATE,
DATAFIM DATE,
SALDOINI NUMERIC(9,2),
CODEMPRESA INTEGER)
RETURNS (
SALDO NUMERIC(15,2),
DATASALDO DATE,
CREDITOS NUMERIC(15,2),
DEBITOS NUMERIC(15,2))
AS
DECLARE VARIABLE ES INTEGER;
DECLARE VARIABLE DATA DATE;
DECLARE VARIABLE VALOR NUMERIC(15,2);
begin
/* SALDO INICIAL */
SALDO = SALDOINI;
/* INICIO */
DATASALDO = NULL;
CREDITOS = NULL;
DEBITOS  = NULL;
/* SELECAO POR DATA */
FOR
SELECT
PA.ES,
CAST(PA.DATAVENCIMENTO AS DATE) AS DATAVENC,
PA.VALOR,
PA.CODEMPRESA
FROM
PAGAMENTOS PA
WHERE
CAST(PA.DATAVENCIMENTO AS DATE) BETWEEN :DATAINI AND :DATAFIM
ORDER BY
PA.DATAVENCIMENTO,
PA.ES
INTO
:ES,
:DATA,
:VALOR,
:CODEMPRESA
DO
BEGIN
/* MUDOU A DATA? */
IF (DATASALDO <> DATA) THEN
BEGIN
/* TENHO LANCAMENTOS ANTERIORES */
IF ((CREDITOS IS NOT NULL) AND (DEBITOS IS NOT NULL)) THEN
BEGIN
SALDO = (SALDO + CREDITOS) - DEBITOS;
SUSPEND;
END
/* DATA ATUAL AGORA */
DATASALDO = DATA;
CREDITOS = NULL;
DEBITOS = NULL;
END
/* CREDITO OU DEBITO? */
IF (ES = 1) THEN
BEGIN
/* COMPUTO OS DEBITOS */
IF (DEBITOS IS NULL) THEN
DEBITOS = VALOR;
ELSE
DEBITOS = DEBITOS + VALOR;
END ELSE
BEGIN
/* COMPUTO OS CREDITOS */
IF (CREDITOS IS NULL) THEN
CREDITOS = VALOR;
ELSE
CREDITOS = CREDITOS + VALOR;
END
END
/* TENHO LANCAMENTOS FINAIS? */
IF ((CREDITOS IS NOT NULL) AND (DEBITOS IS NOT NULL)) THEN
BEGIN
SALDO = (SALDO + CREDITOS) - DEBITOS;
SUSPEND;
END
end
^
SET TERM ; ^
SET TERM ^ ;
ALTER PROCEDURE ANALISE_FLUXOCAIXA_DIA (
DATAINI DATE,
DATAFIM DATE,
SALDOINI NUMERIC(9,2),
CODEMPRESA INTEGER)
RETURNS (
SALDO NUMERIC(15,2),
DATASALDO DATE,
CREDITOS NUMERIC(15,2),
DEBITOS NUMERIC(15,2))
AS
DECLARE VARIABLE ES INTEGER;
DECLARE VARIABLE DATA DATE;
DECLARE VARIABLE VALOR NUMERIC(15,2);
DECLARE VARIABLE DATACONST DATE;
begin
/* CONSTANTE NULA */
DATACONST = '01-01-1990';
/* SALDO INICIAL */
SALDO = SALDOINI;
/* INICIO */
DATASALDO = DATACONST;
CREDITOS = NULL;
DEBITOS  = NULL;
/* SELECAO POR DATA */
FOR
SELECT
PA.ES,
CAST(PA.DATAVENCIMENTO AS DATE) AS DATAVENC,
PA.VALOR,
PA.CODEMPRESA
FROM
PAGAMENTOS PA
WHERE
PA.PAGO = 0 AND
CAST(PA.DATAVENCIMENTO AS DATE) BETWEEN :DATAINI AND :DATAFIM
ORDER BY
PA.DATAVENCIMENTO,
PA.ES
INTO
:ES,
:DATA,
:VALOR,
:CODEMPRESA
DO
BEGIN
/* MUDOU A DATA? */
IF (DATASALDO < DATA) THEN
BEGIN
/* TENHO LANCAMENTOS ANTERIORES */
IF (DATASALDO <> DATACONST) THEN
BEGIN
IF (CREDITOS IS NULL) THEN
CREDITOS = 0;
IF (DEBITOS IS NULL) THEN
DEBITOS = 0;
SALDO = (SALDO + CREDITOS) + DEBITOS;
SUSPEND;
END
/* DATA ATUAL AGORA */
DATASALDO = DATA;
CREDITOS = NULL;
DEBITOS = NULL;
END
/* CREDITO OU DEBITO? */
IF (ES = 1) THEN
BEGIN
VALOR = (VALOR * -1);
/* COMPUTO OS DEBITOS */
IF (DEBITOS IS NULL) THEN
DEBITOS = VALOR;
ELSE
DEBITOS = DEBITOS + VALOR;
END ELSE
BEGIN
/* COMPUTO OS CREDITOS */
IF (CREDITOS IS NULL) THEN
CREDITOS = VALOR;
ELSE
CREDITOS = CREDITOS + VALOR;
END
END
/* TENHO LANCAMENTOS FINAIS? */
IF ((CREDITOS IS NOT NULL) AND (DEBITOS IS NOT NULL)) THEN
BEGIN
SALDO = (SALDO + CREDITOS) + DEBITOS;
SUSPEND;
END
end
^
SET TERM ; ^
GRANT EXECUTE ON PROCEDURE ANALISE_FLUXOCAIXA_DIA TO PUBLIC;
SET TERM ^ ;
CREATE PROCEDURE ANALISE_FLUXOCAIXA_SEMANA (
DATAINI DATE,
DATAFIM DATE,
SALDOINI NUMERIC(9,2),
CODEMPRESA INTEGER)
RETURNS (
SALDO NUMERIC(15,2),
DATASALDO VARCHAR(30),
CREDITOS NUMERIC(15,2),
DEBITOS NUMERIC(15,2))
AS
DECLARE VARIABLE ES INTEGER;
DECLARE VARIABLE DATA VARCHAR(30);
DECLARE VARIABLE VALOR NUMERIC(15,2);
DECLARE VARIABLE DATACONST DATE;
begin
/* CONSTANTE NULA */
DATACONST = 'SEMANA';
/* SALDO INICIAL */
SALDO = SALDOINI;
/* INICIO */
DATASALDO = DATACONST;
CREDITOS = NULL;
DEBITOS  = NULL;
/* SELECAO POR DATA */
FOR
SELECT
PA.ES,
PA.VEN_STR_SEMANA_FLUXO,
PA.VALOR,
PA.CODEMPRESA
FROM
PAGAMENTOS PA
WHERE
PA.PAGO = 0 AND
CAST(PA.DATAVENCIMENTO AS DATE) BETWEEN :DATAINI AND :DATAFIM
ORDER BY
PA.DATAVENCIMENTO,
PA.ES
INTO
:ES,
:DATA,
:VALOR,
:CODEMPRESA
DO
BEGIN
/* MUDOU A DATA? */
IF (DATASALDO <> DATA) THEN
BEGIN
/* TENHO LANCAMENTOS ANTERIORES */
IF (DATASALDO <> DATACONST) THEN
BEGIN
IF (CREDITOS IS NULL) THEN
CREDITOS = 0;
IF (DEBITOS IS NULL) THEN
DEBITOS = 0;
SALDO = (SALDO + CREDITOS) + DEBITOS;
SUSPEND;
END
/* DATA ATUAL AGORA */
DATASALDO = DATA;
CREDITOS = NULL;
DEBITOS = NULL;
END
/* CREDITO OU DEBITO? */
IF (ES = 1) THEN
BEGIN
VALOR = (VALOR * -1);
/* COMPUTO OS DEBITOS */
IF (DEBITOS IS NULL) THEN
DEBITOS = VALOR;
ELSE
DEBITOS = DEBITOS + VALOR;
END ELSE
BEGIN
/* COMPUTO OS CREDITOS */
IF (CREDITOS IS NULL) THEN
CREDITOS = VALOR;
ELSE
CREDITOS = CREDITOS + VALOR;
END
END
/* TENHO LANCAMENTOS FINAIS? */
IF ((CREDITOS IS NOT NULL) AND (DEBITOS IS NOT NULL)) THEN
BEGIN
SALDO = (SALDO + CREDITOS) + DEBITOS;
SUSPEND;
END
end
^
SET TERM ; ^
SET TERM ^ ;
ALTER PROCEDURE ANALISE_FLUXOCAIXA_SEMANA (
DATAINI DATE,
DATAFIM DATE,
SALDOINI NUMERIC(9,2),
CODEMPRESA INTEGER)
RETURNS (
SALDO NUMERIC(15,2),
