begin
/* CONSTANTE NULA */
DATACONST = 'SEMANA';
/* SALDO INICIAL */
SALDO = SALDOINI;
/* INICIO */
DATASALDO = DATACONST;
CREDITOS = NULL;
DEBITOS  = NULL;
