PA.PAGO = 0 AND
CAST(PA.DATAVENCIMENTO AS DATE) BETWEEN :DATAINI AND :DATAFIM
ORDER BY
PA.DATAVENCIMENTO,
PA.ES
INTO
:ES,
:DATA1,
:VALOR,
:CODEMPRESA
DO
BEGIN
/* MUDOU A DATA? */
IF (DATASALDO <> DATA1) THEN
BEGIN
/* TENHO LANCAMENTOS ANTERIORES */
IF (DATASALDO <> DATACONST) THEN
BEGIN
IF (CREDITOS IS NULL) THEN
CREDITOS = 0;
IF (DEBITOS IS NULL) THEN
DEBITOS = 0;
SALDO = (SALDO + CREDITOS) + DEBITOS;
SUSPEND;
END
/* DATA ATUAL AGORA */
DATASALDO = DATA1;
CREDITOS = NULL;
DEBITOS = NULL;
END
/* CREDITO OU DEBITO? */
IF (ES = 1) THEN
BEGIN
VALOR = (VALOR * -1);
/* COMPUTO OS DEBITOS */
IF (DEBITOS IS NULL) THEN
DEBITOS = VALOR;
ELSE
DEBITOS = DEBITOS + VALOR;
END ELSE
BEGIN
/* COMPUTO OS CREDITOS */
IF (CREDITOS IS NULL) THEN
CREDITOS = VALOR;
ELSE
CREDITOS = CREDITOS + VALOR;
END
END
/* TENHO LANCAMENTOS FINAIS? */
IF ((CREDITOS IS NOT NULL) AND (DEBITOS IS NOT NULL)) THEN
BEGIN
SALDO = (SALDO + CREDITOS) + DEBITOS;
SUSPEND;
END
end
^
SET TERM ; ^
GRANT EXECUTE ON PROCEDURE ANALISE_FLUXOCAIXA_SEMANA TO PUBLIC;
