CREATE PROCEDURE ANALISE_FLUXOCAIXA_MES (
DATAINI DATE,
DATAFIM DATE,
SALDOINI NUMERIC(9,2),
CODEMPRESA INTEGER)
RETURNS (
SALDO NUMERIC(15,2),
DATASALDO VARCHAR(30),
CREDITOS NUMERIC(15,2),
DEBITOS NUMERIC(15,2))
AS
DECLARE VARIABLE ES INTEGER;
DECLARE VARIABLE DATA1 VARCHAR(30);
DECLARE VARIABLE VALOR NUMERIC(15,2);
DECLARE VARIABLE DATACONST VARCHAR(30);
begin
/* CONSTANTE NULA */
DATACONST = 'MES';
/* SALDO INICIAL */
SALDO = SALDOINI;
/* INICIO */
DATASALDO = DATACONST;
CREDITOS = NULL;
DEBITOS  = NULL;
/* SELECAO POR DATA */
FOR
SELECT
PA.ES,
PA.VEN_STR_MES_FLUXO,
PA.VALOR,
PA.CODEMPRESA
FROM
PAGAMENTOS PA
WHERE
PA.PAGO = 0 AND
CAST(PA.DATAVENCIMENTO AS DATE) BETWEEN :DATAINI AND :DATAFIM
ORDER BY
PA.DATAVENCIMENTO,
PA.ES
INTO
:ES,
:DATA1,
:VALOR,
:CODEMPRESA
DO
BEGIN
/* MUDOU A DATA? */
IF (DATASALDO <> DATA1) THEN
BEGIN
/* TENHO LANCAMENTOS ANTERIORES */
IF (DATASALDO <> DATACONST) THEN
BEGIN
IF (CREDITOS IS NULL) THEN
CREDITOS = 0;
IF (DEBITOS IS NULL) THEN
DEBITOS = 0;
SALDO = (SALDO + CREDITOS) + DEBITOS;
SUSPEND;
END
/* DATA ATUAL AGORA */
DATASALDO = DATA1;
CREDITOS = NULL;
DEBITOS = NULL;
END
/* CREDITO OU DEBITO? */
IF (ES = 1) THEN
BEGIN
VALOR = (VALOR * -1);
/* COMPUTO OS DEBITOS */
IF (DEBITOS IS NULL) THEN
DEBITOS = VALOR;
ELSE
DEBITOS = DEBITOS + VALOR;
END ELSE
BEGIN
/* COMPUTO OS CREDITOS */
IF (CREDITOS IS NULL) THEN
CREDITOS = VALOR;
ELSE
CREDITOS = CREDITOS + VALOR;
END
END
/* TENHO LANCAMENTOS FINAIS? */
IF ((CREDITOS IS NOT NULL) AND (DEBITOS IS NOT NULL)) THEN
BEGIN
SALDO = (SALDO + CREDITOS) + DEBITOS;
SUSPEND;
END
end
^
SET TERM ; ^
SET TERM ^ ;
ALTER PROCEDURE ANALISE_FLUXOCAIXA_MES (
DATAINI DATE,
DATAFIM DATE,
SALDOINI NUMERIC(9,2),
CODEMPRESA INTEGER)
RETURNS (
SALDO NUMERIC(15,2),
DATASALDO VARCHAR(30),
CREDITOS NUMERIC(15,2),
DEBITOS NUMERIC(15,2))
AS
DECLARE VARIABLE ES INTEGER;
DECLARE VARIABLE DATA1 VARCHAR(30);
DECLARE VARIABLE VALOR NUMERIC(15,2);
DECLARE VARIABLE DATACONST VARCHAR(30);
begin
/* CONSTANTE NULA */
DATACONST = 'MES';
/* SALDO INICIAL */
SALDO = SALDOINI;
/* INICIO */
DATASALDO = DATACONST;
CREDITOS = NULL;
DEBITOS  = NULL;
/* SELECAO POR DATA */
FOR
SELECT
PA.ES,
PA.VEN_STR_MES_FLUXO,
PA.VALOR,
PA.CODEMPRESA
FROM
PAGAMENTOS PA
WHERE
PA.PAGO = 0 AND
CAST(PA.DATAVENCIMENTO AS DATE) BETWEEN :DATAINI AND :DATAFIM
ORDER BY
PA.DATAVENCIMENTO,
PA.ES
INTO
:ES,
:DATA1,
:VALOR,
:CODEMPRESA
DO
BEGIN
/* MUDOU A DATA? */
IF (DATASALDO <> DATA1) THEN
BEGIN
/* TENHO LANCAMENTOS ANTERIORES */
IF (DATASALDO <> DATACONST) THEN
BEGIN
IF (CREDITOS IS NULL) THEN
CREDITOS = 0;
IF (DEBITOS IS NULL) THEN
DEBITOS = 0;
SALDO = (SALDO + CREDITOS) + DEBITOS;
SUSPEND;
END
/* DATA ATUAL AGORA */
DATASALDO = DATA1;
CREDITOS = NULL;
DEBITOS = NULL;
END
/* CREDITO OU DEBITO? */
IF (ES = 1) THEN
BEGIN
VALOR = (VALOR * -1);
/* COMPUTO OS DEBITOS */
IF (DEBITOS IS NULL) THEN
DEBITOS = VALOR;
ELSE
DEBITOS = DEBITOS + VALOR;
END ELSE
BEGIN
/* COMPUTO OS CREDITOS */
IF (CREDITOS IS NULL) THEN
CREDITOS = VALOR;
ELSE
CREDITOS = CREDITOS + VALOR;
END
END
/* TENHO LANCAMENTOS FINAIS? */
IF ((CREDITOS IS NOT NULL) AND (DEBITOS IS NOT NULL)) THEN
BEGIN
SALDO = (SALDO + CREDITOS) + DEBITOS;
SUSPEND;
END
end
^
SET TERM ; ^
GRANT EXECUTE ON PROCEDURE ANALISE_FLUXOCAIXA_MES TO PUBLIC;
INSERT INTO SCRIPTVER(NUMERO, DATA) VALUES (263, CURRENT_TIMESTAMP);
COMMIT;

/*
** PREVENÇÃO DO BUG DO SALDO ANTERIOR NOS PAGAMENTOS
*/
SET TERM ^ ;
ALTER TRIGGER PAGAMENTOS_BU0
ACTIVE BEFORE UPDATE POSITION 0
AS
DECLARE VARIABLE ULTIMO     INTEGER;
DECLARE VARIABLE SALDO      NUMERIC(9, 2);
DECLARE VARIABLE SALDONOVO  NUMERIC(9, 2);
BEGIN
/* CARREGO AS OPCOES DO DOCUMENTO */
SELECT
TIPO_PAG,
TIPO_DOC
FROM
DOCUMENTOS
WHERE
CODIGO = NEW.CODDOCUMENTO
INTO
NEW.TIPO_PAG,
NEW.TIPO_DOC;
/* FOI ESTORNADO? */
IF (NEW.ESTORNO_CODUSUARIO IS NOT NULL) THEN
BEGIN
SELECT
NOMEUSER
FROM
CLIENTES
WHERE
CODIGO = NEW.ESTORNO_CODUSUARIO
INTO
NEW.ESTORNO_NOMEUSER;
END
/* ESTA PAGO? */
IF ((OLD.PAGO = 0) AND (NEW.PAGO = 1)) THEN
BEGIN
/* DESCUBRO O SALDO ANTERIOR */
SELECT
MAX(CODLANCTO)
FROM
PAGAMENTOS
WHERE
CODCONTA = NEW.CODCONTA AND
CODEMPRESA = NEW.CODEMPRESA
INTO
:ULTIMO;
IF (ULTIMO IS NOT NULL) THEN
BEGIN
SELECT FIRST 1
PA.SALDOATU
FROM
PAGAMENTOS PA
WHERE
PA.CODLANCTO = :ULTIMO
ORDER BY
PA.DATAPAGO DESC
INTO
:SALDO;
END ELSE SALDO = 0;
/* PEGO O NUMERO DESTE LANCAMENTO */
SELECT
SEQUENCIA
FROM
SEQ_OBTER('LANCTO')
INTO
:ULTIMO;
/* GRAVO OS VALORES CORRETOS */
NEW.SALDOANT = SALDO;
IF (NEW.ES = 1) THEN
SALDONOVO = SALDO - NEW.TOTAL_PAGO;
IF (NEW.ES = 2) THEN
SALDONOVO = SALDO + NEW.TOTAL_PAGO;
NEW.SALDOATU = SALDONOVO;
NEW.NOMEUSUARIO = USER;
NEW.DATAPAGO = 'NOW';
NEW.CODLANCTO = ULTIMO;
END
END
^
SET TERM ; ^
INSERT INTO SCRIPTVER(NUMERO, DATA) VALUES (274, CURRENT_TIMESTAMP);
COMMIT;

