/*
** MELHORIAS DE PERFORMANCE E CORREÇÃO DE BUGS
*/
create index MOVIMENTOS_SI08 on MOVIMENTOS (DATA_ENTREGA);
SET TERM ^ ;
ALTER TRIGGER STATUS_UPDATE
ACTIVE BEFORE UPDATE POSITION 0
AS
DECLARE VARIABLE PRIMEIRO TIMESTAMP;
DECLARE VARIABLE ULTIMO TIMESTAMP;
DECLARE VARIABLE TOTAL DOUBLE PRECISION;
DECLARE VARIABLE TDATAINI TIMESTAMP;
DECLARE VARIABLE TDATAFIM TIMESTAMP;
BEGIN
/* DATA DA ENTREGA */
IF ((NEW.QUEM_RECEBEU IS NOT NULL) AND (OLD.QUEM_RECEBEU IS NULL)) THEN
NEW.DATA_ENTREGA = CURRENT_TIMESTAMP;
/* DATA DA AUTORIZAÇÃO */
IF ((NEW.QUEM_AUTORIZOU IS NOT NULL) AND (OLD.QUEM_AUTORIZOU IS NULL)) THEN
NEW.DATA_AUTORIZADO = CURRENT_TIMESTAMP;
IF (NEW.TIPO = 1) THEN
BEGIN
IF (NEW.ES = 0) THEN
BEGIN
NEW.STATUS = 'ORC ABERTO';
IF (NEW.AUTORIZADO = 'S') THEN
BEGIN
NEW.STATUS = 'ORC AUTORIZADO';
END ELSE
BEGIN
IF (NEW.DATA_AUTORIZADO IS NOT NULL) THEN
NEW.STATUS = 'ORC NAO AUTO';
IF ((NEW.RETORNO = 'S') AND (NEW.DATA_RETORNO IS NOT NULL)) THEN
NEW.STATUS = 'ORC RETORNAR';
END
END
IF (NEW.ES = 1) THEN
BEGIN
if (NEW.FECHADO = 'S') then
NEW.STATUS = 'COMPRA';
ELSE
NEW.STATUS = 'PED COMPRA';
END
END
IF (NEW.TIPO = 2) THEN
BEGIN
NEW.STATUS = 'OS ABERTA';
/* VERIFICO SE O TECNICO JÁ INICIOU */
SELECT FIRST 1
DATAINI,
DATAFIM
FROM
OS_VENDER
WHERE
CODMOVIMENTO = NEW.CODIGO
INTO
:TDATAINI,
:TDATAFIM;
IF (TDATAINI IS NOT NULL) THEN
BEGIN
NEW.STATUS = 'OS INICIADA';
IF (NEW.DATA_AUTORIZADO IS NOT NULL) THEN
BEGIN
IF (NEW.AUTORIZADO = 'N') THEN
NEW.STATUS = 'OS NAO AUTO';
ELSE
IF (TDATAFIM IS NULL) THEN
NEW.STATUS = 'OS AUTORIZADA';
ELSE
NEW.STATUS = 'OS CONCLUIDA';
END
IF (NEW.DATA_ENTREGA IS NOT NULL) THEN
NEW.STATUS = 'OS ENTREGUE';
END
END
IF (NEW.TIPO = 3) THEN NEW.STATUS = 'OS FECHADA';
IF (NEW.TIPO = 4) THEN NEW.STATUS = 'PRE ASSITENCIA';
IF (NEW.TIPO = 5) THEN NEW.STATUS = 'VENDA ABERTA';
IF (NEW.TIPO = 6) THEN NEW.STATUS = 'VENDA FECHADA';
IF (NEW.TIPO = 7) THEN NEW.STATUS = 'N/F';
IF (NEW.TIPO = 8) THEN NEW.STATUS = 'REMESSA';
IF (NEW.TIPO = 9) THEN NEW.STATUS = 'ESTORNO';
/* VERIFICO O NUMERADOR */
EXECUTE PROCEDURE VALIDA_NUMERADOR(NEW.NUMERADOR, NEW.CODCLIENTE,
NEW.CODMARCA, NEW.CODMODELO, NEW.SERIE);
/* calcular impostos */
IF ((NEW.ES = 2)                  AND
(NEW.TIPO IN (3, 6))          AND
(OLD.TIPO IN (2, 5)))         THEN
BEGIN
/* TOTAL ICMS */
EXECUTE PROCEDURE CALC_ICMS(NEW.CODIGO)
RETURNING_VALUES NEW.VALOR_ICMS, NEW.VALOR_IPI_PRODUTOS;
IF (NEW.NO_ICMS = 'N') THEN
NEW.VALOR_ICMS = 0;
IF (NEW.NO_IPI = 'N') THEN
NEW.VALOR_IPI_PRODUTOS = 0;
END
/* FECHOU A VENDA */
IF ((OLD.TIPO IN (2, 5)) AND (NEW.TIPO IN (3, 6))) THEN
BEGIN
/* USUÁRIO, DATA E HORA DO FECHAMENTO */
NEW.DATAFECHAMENTO = 'NOW';
NEW.FECHADO = 'S';
IF ((UDF_TRIM(NEW.USUARIOFECHAMENTO) = '') OR (NEW.USUARIOFECHAMENTO IS NULL)) THEN
NEW.USUARIOFECHAMENTO = USER;
/* VINCULAR SERIAL NA VENDA FECHADA */
IF (NEW.TIPO = 6) THEN
EXECUTE PROCEDURE VINCULO_SERIE NEW.CODIGO
RETURNING_VALUES NEW.SERIE;
/* TEMPOS DE ATENDIMENTO, SE FOR OS */
IF (NEW.TIPO = 3) THEN
BEGIN
SELECT
MIN(DATAINI),
MAX(DATAFIM),
SUM(THORAS)
FROM
OS_VENDER
WHERE
CODMOVIMENTO = NEW.CODIGO
INTO
:PRIMEIRO,
:ULTIMO,
:TOTAL;
NEW.TEMPO_CHEGADA    = CAST(
UDF_MINUTESBETWEEN(NEW.DATA, PRIMEIRO)
AS DOUBLE PRECISION) / 60;
NEW.TEMPO_CONCLUSAO  = CAST(
UDF_MINUTESBETWEEN(NEW.DATA, ULTIMO)
AS DOUBLE PRECISION) / 60;
NEW.TEMPO_LANCAMENTO = CAST(
UDF_MINUTESBETWEEN(ULTIMO, NEW.DATAFECHAMENTO)
AS DOUBLE PRECISION) / 60;
NEW.TEMPO_TECNICOS = TOTAL;
NEW.DATAINICIO  = PRIMEIRO;
NEW.DATATERMINO = ULTIMO;
IF (PRIMEIRO > NEW.DATAPREVISAO) THEN
NEW.TEMPO_FORADOPRAZO = CAST(
UDF_MINUTESBETWEEN(NEW.DATAPREVISAO, PRIMEIRO)
AS DOUBLE PRECISION) / 60;
ELSE
NEW.TEMPO_FORADOPRAZO = 0;
END
NEW.TEMPO_CICLO = CAST(
UDF_MINUTESBETWEEN(NEW.DATA, NEW.DATAFECHAMENTO)
AS DOUBLE PRECISION) / 60;
END
/* RATEIO DOS CUSTOS DE COMPRA */
IF ((NEW.ES = 1) AND (NEW.TIPO = 1)) THEN
EXECUTE PROCEDURE RATEIO_CUSTOS (
NEW.CODIGO,
NEW.VALOR_PRODUTOS,
NEW.VALOR_RATEIO_CUSTO);
END
^
SET TERM ; ^
INSERT INTO SCRIPTVER(NUMERO, DATA) VALUES (280, CURRENT_TIMESTAMP);
COMMIT;

/*
** PRODUÇÃO - CADASTRO DE MATERIA PRIMA E COMPOSICAO DE PRODUTO
*/
SET TERM ^ ;
ALTER TRIGGER VERIFICA_BARRA_UPD
INACTIVE BEFORE UPDATE POSITION 0
AS
DECLARE VARIABLE TEMP_EAN VARCHAR(13);
BEGIN
IF (NEW.INDIVIDUAL = 'N') THEN
BEGIN
EXECUTE PROCEDURE CALC_EAN13(NEW.BARRA)
RETURNING_VALUES :TEMP_EAN;
IF (NEW.BARRA <> TEMP_EAN) THEN
EXCEPTION PRODUTO_BARRA;
END
IF ((NEW.PRECOCUSTO < 0) OR (NEW.PRECOVENDA < 0)) THEN
EXCEPTION VALOR_ZERO;
IF (NEW.PRECOCUSTO > NEW.PRECOVENDA) THEN
EXCEPTION PRECOCUSTO_MAIOR;
END
^
SET TERM ; ^
SET TERM ^ ;
ALTER TRIGGER PRODUTOS_MARGEM_UPD
INACTIVE BEFORE UPDATE POSITION 200
AS
BEGIN
IF ((NEW.PRECOCUSTO = 0) AND (NEW.PRECOVENDA = 0)) THEN
BEGIN
NEW.MARGEM = 0;
END ELSE
BEGIN
IF ((NEW.PRECOCUSTO = 0) AND (NEW.PRECOVENDA > 0)) THEN
BEGIN
NEW.MARGEM = NEW.PRECOVENDA * 100;
END ELSE
BEGIN
NEW.MARGEM = (100 - ((NEW.PRECOVENDA * 100) / NEW.PRECOCUSTO)) * -1;
END
END
IF (NEW.CODSERVICO IS NULL) THEN
BEGIN
NEW.PRECOSERVICO = 0;
NEW.PRECOTOTAL   = NEW.PRECOVENDA;
END ELSE NEW.PRECOTOTAL = NEW.PRECOSERVICO + NEW.PRECOVENDA;
END
^
SET TERM ; ^
ALTER TABLE PRODUTOS ADD MATPRIMA SIMNAO;
UPDATE PRODUTOS SET MATPRIMA = 'N';
SET TERM ^ ;
ALTER TRIGGER PRODUTOS_MARGEM_UPD
ACTIVE BEFORE UPDATE POSITION 200
AS
BEGIN
IF ((NEW.PRECOCUSTO = 0) AND (NEW.PRECOVENDA = 0)) THEN
BEGIN
NEW.MARGEM = 0;
END ELSE
BEGIN
IF ((NEW.PRECOCUSTO = 0) AND (NEW.PRECOVENDA > 0)) THEN
BEGIN
NEW.MARGEM = NEW.PRECOVENDA * 100;
END ELSE
BEGIN
NEW.MARGEM = (100 - ((NEW.PRECOVENDA * 100) / NEW.PRECOCUSTO)) * -1;
END
END
IF (NEW.CODSERVICO IS NULL) THEN
BEGIN
NEW.PRECOSERVICO = 0;
NEW.PRECOTOTAL   = NEW.PRECOVENDA;
END ELSE NEW.PRECOTOTAL = NEW.PRECOSERVICO + NEW.PRECOVENDA;
END
^
SET TERM ; ^
SET TERM ^ ;
ALTER TRIGGER VERIFICA_BARRA_UPD
ACTIVE BEFORE UPDATE POSITION 0
AS
DECLARE VARIABLE TEMP_EAN VARCHAR(13);
BEGIN
IF (NEW.INDIVIDUAL = 'N') THEN
BEGIN
EXECUTE PROCEDURE CALC_EAN13(NEW.BARRA)
RETURNING_VALUES :TEMP_EAN;
IF (NEW.BARRA <> TEMP_EAN) THEN
EXCEPTION PRODUTO_BARRA;
END
IF ((NEW.PRECOCUSTO < 0) OR (NEW.PRECOVENDA < 0)) THEN
EXCEPTION VALOR_ZERO;
IF (NEW.PRECOCUSTO > NEW.PRECOVENDA) THEN
EXCEPTION PRECOCUSTO_MAIOR;
END
^
SET TERM ; ^
INSERT INTO SCRIPTVER(NUMERO, DATA) VALUES (281, CURRENT_TIMESTAMP);
COMMIT;

/*
** ANALISE ESTATISTICA DOS PRODUTOS VENDIDOS
*/
ALTER TABLE INDIVIDUAIS ADD DATAENTRADA DATA;
ALTER TABLE INDIVIDUAIS ADD DATASAIDA DATA;
ALTER TABLE INDIVIDUAIS ADD TEMPOESTOQUE DOUBLEPRES;
SET TERM ^ ;
CREATE TRIGGER INDIVIDUAIS_BUESTOQUE FOR INDIVIDUAIS
ACTIVE BEFORE UPDATE POSITION 1000
AS
BEGIN
/* ESTATISTICA DE GIRO DE ESTOQUE */
IF ((NEW.VENDIDO = 'S') AND (OLD.VENDIDO = 'N')) THEN
BEGIN
/* DATA DA COMPRA */
SELECT
MO.DATA
FROM
MOVIMENTOS MO
WHERE
MO.CODIGO = NEW.CODMOVENTRADA
INTO
NEW.DATAENTRADA;
/* DATA DA VENDA */
SELECT
MO.DATA
FROM
MOVIMENTOS MO
WHERE
MO.CODIGO = NEW.CODMOVSAIDA
INTO
NEW.DATASAIDA;
/* TEMPO NO ESTOQUE */
NEW.TEMPOESTOQUE = CAST((NEW.DATASAIDA - NEW.DATAENTRADA) AS DOUBLE PRECISION);
END
END
^
SET TERM ; ^
INSERT INTO SCRIPTVER(NUMERO, DATA) VALUES (282, CURRENT_TIMESTAMP);
COMMIT;

/*
** SUPORTE A MATERIA PRIMA
*/
CREATE DOMAIN PSM_REQDEF AS
CHAR(1) CHARACTER SET NONE
DEFAULT 'P'
CHECK (VALUE IN ('P', 'S', 'M'))
COLLATE NONE;
update RDB$RELATION_FIELDS set
RDB$FIELD_SOURCE = 'PSM_REQDEF'
where (RDB$FIELD_NAME = 'PS') and
(RDB$RELATION_NAME = 'INDIVIDUAIS');
update RDB$RELATION_FIELDS set
RDB$FIELD_SOURCE = 'PSM_REQDEF'
where (RDB$FIELD_NAME = 'PS') and
(RDB$RELATION_NAME = 'PRODUTOS');
INSERT INTO SCRIPTVER(NUMERO, DATA) VALUES (283, CURRENT_TIMESTAMP);
COMMIT;

/*
** COMPOSICAO DE PRODUTO ACABADO
*/
CREATE TABLE COMPOSICAO (
CODPRODUTO INTEIRO_VALIDO NOT NULL,
CODMATPRIMA INTEIRO_VALIDO NOT NULL,
QUANTIDADE DINHEIRO);
ALTER TABLE COMPOSICAO
ADD CONSTRAINT PK_COMPOSICAO
PRIMARY KEY (CODPRODUTO,CODMATPRIMA);
GRANT ALL ON COMPOSICAO TO PUBLIC;
ALTER TABLE COMPOSICAO
ADD CONSTRAINT COMPOSICAO_FK01
FOREIGN KEY (CODPRODUTO)
REFERENCES PRODUTOS(CODIGO)
ON UPDATE CASCADE;
ALTER TABLE COMPOSICAO
ADD CONSTRAINT COMPOSICAO_FK02
FOREIGN KEY (CODMATPRIMA)
REFERENCES PRODUTOS(CODIGO)
ON UPDATE CASCADE;
ALTER TABLE COMPOSICAO ADD CONVERTIDO DINHEIRO;
CREATE EXCEPTION MATPRIMA_ERRADA 'PRODUTO ACABADO NÃO PODE SER MATÉRIA PRIMA.';
SET TERM ^ ;
CREATE TRIGGER COMPOSICAO_BI0 FOR COMPOSICAO
ACTIVE BEFORE INSERT POSITION 0
AS
DECLARE VARIABLE PSM CHAR(1);
BEGIN
/* NÃO PODE SER O MESMO */
IF (NEW.CODPRODUTO = NEW.CODMATPRIMA) THEN
EXCEPTION MATPRIMA_ERRADA;
/* PRODUTO TEM QUE SER PRODUTO ACABADO */
SELECT
PO.PS
FROM
PRODUTOS PO
WHERE
PO.CODIGO = NEW.CODPRODUTO
INTO
:PSM;
IF (PSM <> 'P') THEN
EXCEPTION MATPRIMA_ERRADA;
/* MATPRIMA TEM QUE SER MATERIA PRIMA */
SELECT
PO.PS
FROM
PRODUTOS PO
WHERE
PO.CODIGO = NEW.CODMATPRIMA
INTO
:PSM;
IF (PSM <> 'M') THEN
EXCEPTION MATPRIMA_ERRADA;
END
^
SET TERM ; ^
SET TERM ^ ;
CREATE TRIGGER COMPOSICAO_BU0 FOR COMPOSICAO
ACTIVE BEFORE UPDATE POSITION 0
AS
DECLARE VARIABLE PSM CHAR(1);
BEGIN
/* NÃO PODE SER O MESMO */
IF (NEW.CODPRODUTO = NEW.CODMATPRIMA) THEN
EXCEPTION MATPRIMA_ERRADA;
/* PRODUTO TEM QUE SER PRODUTO ACABADO */
SELECT
PO.PS
FROM
PRODUTOS PO
WHERE
PO.CODIGO = NEW.CODPRODUTO
INTO
:PSM;
IF (PSM <> 'P') THEN
EXCEPTION MATPRIMA_ERRADA;
/* MATPRIMA TEM QUE SER MATERIA PRIMA */
SELECT
PO.PS
FROM
PRODUTOS PO
WHERE
PO.CODIGO = NEW.CODMATPRIMA
INTO
:PSM;
IF (PSM <> 'M') THEN
EXCEPTION MATPRIMA_ERRADA;
END
^
SET TERM ; ^
INSERT INTO SCRIPTVER(NUMERO, DATA) VALUES (286, CURRENT_TIMESTAMP);
COMMIT;

/*
** SUPORTE A COMPRA DE MATERIA PRIMA
*/
SET TERM ^ ;
ALTER TRIGGER ORSOMA_INS
ACTIVE AFTER INSERT POSITION 0
AS
DECLARE VARIABLE SOMA NUMERIC(9,2);
DECLARE VARIABLE CODIGO INTEGER;
DECLARE VARIABLE ES CHAR(1);
DECLARE VARIABLE TIPO CHAR(1);
DECLARE VARIABLE ETIQUETA CHAR(1);
DECLARE VARIABLE NUMETIQ INTEGER;
DECLARE VARIABLE INDIVIDUAL CHAR(1);
DECLARE VARIABLE SEQUENCIA INTEGER;
DECLARE VARIABLE SEQUENSTR CHAR(4);
DECLARE VARIABLE N INTEGER;
DECLARE VARIABLE C INTEGER;
DECLARE VARIABLE BARRA CHAR(14);
DECLARE VARIABLE BARRA_TEMP CHAR(14);
DECLARE VARIABLE PS CHAR(1);
DECLARE VARIABLE EMPRESA INTEGER;
DECLARE VARIABLE CUSTO_PRODUTO NUMERIC(9,2);
DECLARE VARIABLE CUSTO_IPI NUMERIC(9,2);
BEGIN
/* VERIFICO O TIPO DO MOVIMENTO */
SELECT
ES,
TIPO
FROM
MOVIMENTOS
WHERE
CODIGO = NEW.CODMOVIMENTO
INTO
ES,
TIPO;
IF ((ES IN (0, 1)) AND (TIPO <> 9)) THEN
BEGIN
/* RECUPERO O CODIGO DE BARRAS E O TIPO (PRODUTO, SERVIÇO */
SELECT
BARRA,
PS
FROM
PRODUTOS
WHERE
CODIGO = NEW.CODPRODUTO
INTO
:BARRA,
:PS;
/* SE NAO TIVER CODIGO DE BARRAS GERO ERRO */
IF (BARRA = '0000000000000') THEN
EXCEPTION BARRA_NECESSARIA;
/* VERIFICO SE EH PRODUTO OU SERVIÇO */
IF ((PS = 'P') OR (PS = 'M')) THEN
BEGIN
/* PRODUTO */
/* GUARDO O CODIGO DO MOVIMENTO */
CODIGO = NEW.CODMOVIMENTO;
/* RECUPERO O TIPO E A EMPRESA */
SELECT
ES,
TIPO,
CODEMPRESA
FROM
MOVIMENTOS
WHERE
CODIGO = :CODIGO
INTO
:ES,
:TIPO,
:EMPRESA;
/* SE FOR COMPRA */
IF ((ES = 1) AND (TIPO = 1)) THEN
BEGIN
/* ATUALIZO O PREÇO DE CUSTO */
UPDATE
PRODUTOS
SET
PRECOCUSTO = NEW.VALOR_UNITARIO
WHERE
CODIGO = NEW.CODPRODUTO;
/* VERIFICO SE PRECISO REAJUSTAR O PREÇO DE VENDA */
IF (NEW.REAJUSTAR = 'S') THEN
/* REAJUSTO O PREÇO DE VENDA */
UPDATE
PRODUTOS
SET
PRECOVENDA = NEW.VALOR_VENDA,
ICMS = NEW.ICMSVENDA
WHERE
CODIGO = NEW.CODPRODUTO;
/* RECUPERO DADOS DO PRODUTO PARA GERAÇAO */
SELECT
ETIQUETA,
INDIVIDUAL,
SEQUENCIA,
BARRA
FROM
PRODUTOS
WHERE
CODIGO = NEW.CODPRODUTO
INTO
:ETIQUETA,
:INDIVIDUAL,
:SEQUENCIA,
:BARRA;
/* ZERO O CONTADOR */
N = 0;
/* CALCULO O CUSTO DO PRODUTO, IPI */
CUSTO_PRODUTO = (NEW.VALOR_UNITARIO - NEW.DESCONTO);
CUSTO_IPI = (CUSTO_PRODUTO * (NEW.IPI / 100));
/* SE O PRODUTO FOR INDIVIDUAL */
IF (INDIVIDUAL = 'S') THEN
BEGIN
/* EXECUTO A MESMA SEQUENCIA PARA CADA ITEM */
WHILE (N < NEW.QUANTIDADE) DO
BEGIN
/* INCREMENTO O CONTADOR */
N = N + 1;
/* INCREMENTO O CODIGO SEQUENCIAL DO PRODUTO */
SEQUENCIA = SEQUENCIA + 1;
/* GERO A SEQUENCIA EM FORMATO STRING, ALINHADO A DIREITA */
IF (SEQUENCIA > 999) THEN
SEQUENSTR = SEQUENCIA;
ELSE
IF (SEQUENCIA > 99) THEN SEQUENSTR = ('0' || SEQUENCIA);
ELSE IF (SEQUENCIA > 9) THEN SEQUENSTR = ('00' || SEQUENCIA);
ELSE IF (SEQUENCIA > 0) THEN SEQUENSTR = ('000' || SEQUENCIA);
ELSE SEQUENSTR = '0000';
/* GERO O CODIGO DE BARRAS INDIVIDUAL */
BARRA_TEMP = '999' || NEW.CODPRODUTO || SEQUENSTR || '0';
EXECUTE PROCEDURE CALC_EAN13(:BARRA_TEMP)
RETURNING_VALUES :BARRA;
/* RECUPERO A SEQUENCIA DE GRAVAÇAO DA TABELA */
EXECUTE PROCEDURE SEQ_OBTER 'INDIVIDUAIS'
RETURNING_VALUES C;
/* GRAVO O ITEM NA TABELA */
INSERT INTO
INDIVIDUAIS (
CODIGO,
CODITEM,
CODPRODUTO,
CODMOVENTRADA,
SEQUENCIA,
ICMSVENDA,
VALOR_VENDA,
IMPETIQ,
QUANTIDADE,
UNICO,
BARRA,
CODEMPRESA,
CUSTO_PRODUTO,
CUSTO_IPI)
VALUES (
:C,
NEW.CODIGO,
NEW.CODPRODUTO,
NEW.CODMOVIMENTO,
:SEQUENSTR,
NEW.ICMSVENDA,
NEW.VALOR_VENDA,
:ETIQUETA,
1,
1,
:BARRA,
:EMPRESA,
:CUSTO_PRODUTO,
:CUSTO_IPI);
END /* WHILE */
/* ATUALIZO A SEQUENCIA DO PRODUTO UTILIZADA */
UPDATE
PRODUTOS
SET
SEQUENCIA = (SEQUENCIA + NEW.QUANTIDADE)
WHERE
CODIGO = NEW.CODPRODUTO;
END ELSE
/* PRODUTO COMUM - NAO INDIVIDUAL */
BEGIN
/* RECUPERO A SEQUENCIA DA TABELA */
EXECUTE PROCEDURE SEQ_OBTER 'INDIVIDUAIS'
RETURNING_VALUES C;
/* GRAVO OS DADOS NA TABELA */
INSERT INTO
INDIVIDUAIS (
CODIGO,
CODITEM,
CODPRODUTO,
CODMOVENTRADA,
SEQUENCIA,
ICMSVENDA,
VALOR_VENDA,
IMPETIQ,
QUANTIDADE,
UNICO,
BARRA,
CODEMPRESA,
CUSTO_PRODUTO,
CUSTO_IPI)
VALUES (
:C,
NEW.CODIGO,
NEW.CODPRODUTO,
NEW.CODMOVIMENTO,
'0000',
NEW.ICMSVENDA,
NEW.VALOR_VENDA,
:ETIQUETA,
NEW.QUANTIDADE,
0,
:BARRA,
:EMPRESA,
:CUSTO_PRODUTO,
:CUSTO_IPI);
END
END
IF (ES IN (0, 1)) THEN
/* ATUALIZO O VALOR DOS ITENS E DO IPI */
UPDATE
MOVIMENTOS
SET
VALOR_ITENS = VALOR_ITENS + NEW.VALOR_TOTAL,
VALOR_IPI_PRODUTOS = VALOR_IPI_PRODUTOS + NEW.VALOR_IPI
WHERE
CODIGO = :CODIGO;
END
END
END
^
SET TERM ; ^
INSERT INTO SCRIPTVER(NUMERO, DATA) VALUES (287, CURRENT_TIMESTAMP);
COMMIT;

/*
** AUTODESTRUICAO
*/
SET TERM ^ ;
ALTER PROCEDURE REINICIAR (
ROTINA INTEGER)
AS
DECLARE VARIABLE CODCLIENTE1 INTEGER;
DECLARE VARIABLE CODCLIENTE2 INTEGER;
begin
/* DETERMINO A ROTINA */
IF (ROTINA IS NULL) THEN
ROTINA = 0;
/* LIMPAR TUDO */
IF (ROTINA = 0) THEN
BEGIN
/* SEQUENCIA */
DELETE FROM
ITSEQUENCIA;
/* PAGAMENTOS */
UPDATE
PAGAMENTOS
SET PAGO = 0;
DELETE FROM
COMISSOES;
DELETE FROM
PAGAMENTOS;
UPDATE
SEQUENCIA
SET
SEQUENCIA = 1
WHERE
TABELA = 'PAGAMENTOS';
/* ESTOQUE */
UPDATE
INDIVIDUAIS
SET
VENDIDO = 'N';
DELETE FROM
INDIVIDUAIS;
DELETE FROM
TEMPITENS;
UPDATE
SEQUENCIA
SET
SEQUENCIA = 1
WHERE
TABELA = 'INDIVIDUAIS';
UPDATE
SEQUENCIA
SET
SEQUENCIA = 1
WHERE
TABELA = 'TEMPITENS';
/* PRODUTOS */
DELETE FROM
VARIACAO_VALOR;
DELETE FROM
MENSAGENS;
DELETE FROM
CONTRATOS;
DELETE FROM
COMPOSICAO;
DELETE FROM
PRODUTOS;
UPDATE
SEQUENCIA
SET
SEQUENCIA = 10000
WHERE
TABELA = 'PRODUTOS';
/* MOVIMENTOS */
DELETE FROM
OS_VENDER;
DELETE FROM
MOVIMENTOS;
UPDATE
SEQUENCIA
SET
SEQUENCIA = 10000
WHERE
TABELA = 'MOVIMENTOS';
/* EMPRESAS */
DELETE FROM
TRANSFERENCIAS;
DELETE FROM
SISCONFIG
WHERE
CODIGO > 1;
/* CLIENTES */
SELECT
SI.CODCLIENTE,
SI.CODCLIENTE_PADRAO
FROM
SISCONFIG SI
WHERE
SI.CODIGO = 1
INTO
:CODCLIENTE1,
:CODCLIENTE2;
DELETE FROM
CLIENTES
WHERE
CODIGO NOT IN (:CODCLIENTE1, :CODCLIENTE2);
UPDATE
SEQUENCIA
SET
SEQUENCIA = UDF_MAX(:CODCLIENTE1, :CODCLIENTE2) + 1
WHERE
TABELA = 'CLIENTES';
UPDATE
CLIENTES
SET
NOMEUSER = 'SYSDBA'
WHERE
CODIGO = :CODCLIENTE1;
END
end
^
SET TERM ; ^
INSERT INTO SCRIPTVER(NUMERO, DATA) VALUES (293, CURRENT_TIMESTAMP);
COMMIT;

