*/
ALTER TABLE VARIACAO_VALOR DROP CONSTRAINT VARIACAO_VALOR_FK01;
ALTER TABLE VARIACAO_VALOR
ADD CONSTRAINT VARIACAO_VALOR_FK01
FOREIGN KEY (CODPRODUTO)
REFERENCES PRODUTOS(CODIGO)
ON DELETE CASCADE
ON UPDATE CASCADE;
INSERT INTO SCRIPTVER(NUMERO, DATA) VALUES (297, CURRENT_TIMESTAMP);
COMMIT;

/*
** TABELA DE ALOCAÇÃO DE MATÉRIA PRIMA (PRÉ-PRODUÇÃO) 
*/
CREATE TABLE ITPRODUCAO (
CODMOVIMENTO INTEIRO_VALIDO NOT NULL,
CODMATPRIMA INTEIRO_VALIDO NOT NULL,
QUANTIDADE DINHEIRO,
VALOR_UNITARIO DINHEIRO,
VALOR_TOTAL DINHEIRO);
ALTER TABLE ITPRODUCAO
ADD CONSTRAINT PK_ITPRODUCAO
PRIMARY KEY (CODMOVIMENTO,CODMATPRIMA);
GRANT ALL ON ITPRODUCAO TO PUBLIC;
INSERT INTO SCRIPTVER(NUMERO, DATA) VALUES (300, CURRENT_TIMESTAMP);
COMMIT;

/*
** PROCESSO DE PRODUÇÃO II
*/
SET TERM ^ ;
ALTER TRIGGER STATUS_INSERT
ACTIVE BEFORE INSERT POSITION 0
AS
BEGIN
IF (NEW.TIPO = 1) THEN
BEGIN
IF (NEW.ES = 0) THEN
NEW.STATUS = 'ORC ABERTO';
IF (NEW.ES = 1) THEN
NEW.STATUS = 'COMPRA';
END
IF (NEW.TIPO = 2) THEN NEW.STATUS = 'OS ABERTA';
IF (NEW.TIPO = 3) THEN NEW.STATUS = 'OS FECHADA';
IF (NEW.TIPO = 4) THEN
if (NEW.CODCOMPRA IS NULL) then
NEW.STATUS = 'PRE PRODUÇÃO';
ELSE
NEW.STATUS = 'PRODUÇÃO';
IF (NEW.TIPO = 5) THEN NEW.STATUS = 'VENDA ABERTA';
IF (NEW.TIPO = 6) THEN NEW.STATUS = 'VENDA FECHADA';
IF (NEW.TIPO = 7) THEN NEW.STATUS = 'N/F';
IF (NEW.TIPO = 8) THEN NEW.STATUS = 'REMESSA';
IF (NEW.TIPO = 9) THEN NEW.STATUS = 'ESTORNADO';
/* VERIFICO O NUMERADOR */
EXECUTE PROCEDURE VALIDA_NUMERADOR(NEW.NUMERADOR, NEW.CODCLIENTE,
NEW.CODMARCA, NEW.CODMODELO, NEW.SERIE);
/* BASES DE CALCULO DO ICMS E ISS */
SELECT
ALIQUOTA_ISS,
ALIQUOTA_ICMSSIMPLES,
ICMSSIMPLES
FROM
SISCONFIG
WHERE
CODIGO = NEW.CODEMPRESA
INTO
NEW.ISSALIQ,
NEW.ICMSSIMPALIQ,
NEW.ICMSSIMPLES;
/* CONFIGURAÇÕES DA NATUREZA DE OPERAÇÃO */
SELECT
VENDA,
ESTOQUE,
ICMS,
IPI
FROM
NATUOPER
WHERE
CODIGO = NEW.NOTA_CODNATUOPER
INTO
NEW.NO_VENDA,
NEW.NO_ESTOQUE,
NEW.NO_ICMS,
NEW.NO_IPI;
/* INICIALIZAÇÃO DO RETORNO */
NEW.RETORNO = 'N';
END
^
SET TERM ; ^
SET TERM ^ ;
ALTER TRIGGER STATUS_UPDATE
ACTIVE BEFORE UPDATE POSITION 0
AS
DECLARE VARIABLE PRIMEIRO TIMESTAMP;
DECLARE VARIABLE ULTIMO TIMESTAMP;
DECLARE VARIABLE TOTAL DOUBLE PRECISION;
DECLARE VARIABLE TDATAINI TIMESTAMP;
DECLARE VARIABLE TDATAFIM TIMESTAMP;
BEGIN
/* DATA DA ENTREGA */
IF ((NEW.QUEM_RECEBEU IS NOT NULL) AND (OLD.QUEM_RECEBEU IS NULL)) THEN
NEW.DATA_ENTREGA = CURRENT_TIMESTAMP;
/* DATA DA AUTORIZAÇÃO */
IF ((NEW.QUEM_AUTORIZOU IS NOT NULL) AND (OLD.QUEM_AUTORIZOU IS NULL)) THEN
NEW.DATA_AUTORIZADO = CURRENT_TIMESTAMP;
IF (NEW.TIPO = 1) THEN
BEGIN
IF (NEW.ES = 0) THEN
BEGIN
NEW.STATUS = 'ORC ABERTO';
IF (NEW.AUTORIZADO = 'S') THEN
BEGIN
NEW.STATUS = 'ORC AUTORIZADO';
END ELSE
BEGIN
IF (NEW.DATA_AUTORIZADO IS NOT NULL) THEN
NEW.STATUS = 'ORC NAO AUTO';
IF ((NEW.RETORNO = 'S') AND (NEW.DATA_RETORNO IS NOT NULL)) THEN
NEW.STATUS = 'ORC RETORNAR';
END
END
IF (NEW.ES = 1) THEN
BEGIN
if (NEW.FECHADO = 'S') then
NEW.STATUS = 'COMPRA';
ELSE
NEW.STATUS = 'PED COMPRA';
END
END
IF (NEW.TIPO = 2) THEN
BEGIN
NEW.STATUS = 'OS ABERTA';
/* VERIFICO SE O TECNICO JÁ INICIOU */
SELECT FIRST 1
DATAINI,
DATAFIM
FROM
OS_VENDER
WHERE
CODMOVIMENTO = NEW.CODIGO
INTO
:TDATAINI,
:TDATAFIM;
IF (TDATAINI IS NOT NULL) THEN
BEGIN
NEW.STATUS = 'OS INICIADA';
IF (NEW.DATA_AUTORIZADO IS NOT NULL) THEN
BEGIN
IF (NEW.AUTORIZADO = 'N') THEN
NEW.STATUS = 'OS NAO AUTO';
ELSE
IF (TDATAFIM IS NULL) THEN
NEW.STATUS = 'OS AUTORIZADA';
ELSE
NEW.STATUS = 'OS CONCLUIDA';
END
IF (NEW.DATA_ENTREGA IS NOT NULL) THEN
NEW.STATUS = 'OS ENTREGUE';
END
IF (NEW.TIPO = 3) THEN NEW.STATUS = 'OS FECHADA';
IF (NEW.TIPO = 4) THEN
if (NEW.CODCOMPRA IS NULL) then
NEW.STATUS = 'PRE PRODUÇÃO';
ELSE
NEW.STATUS = 'PRODUÇÃO';
IF (NEW.TIPO = 5) THEN NEW.STATUS = 'VENDA ABERTA';
IF (NEW.TIPO = 6) THEN NEW.STATUS = 'VENDA FECHADA';
IF (NEW.TIPO = 7) THEN NEW.STATUS = 'N/F';
IF (NEW.TIPO = 8) THEN NEW.STATUS = 'REMESSA';
IF (NEW.TIPO = 9) THEN NEW.STATUS = 'ESTORNO';
END
/* VERIFICO O NUMERADOR */
EXECUTE PROCEDURE VALIDA_NUMERADOR(NEW.NUMERADOR, NEW.CODCLIENTE,
NEW.CODMARCA, NEW.CODMODELO, NEW.SERIE);
/* calcular impostos */
IF ((NEW.ES = 2) AND
(NEW.TIPO IN (3, 6)) AND
(OLD.TIPO IN (2, 5))) THEN
BEGIN
/* TOTAL ICMS */
EXECUTE PROCEDURE CALC_ICMS(NEW.CODIGO)
RETURNING_VALUES NEW.VALOR_ICMS, NEW.VALOR_IPI_PRODUTOS;
IF (NEW.NO_ICMS = 'N') THEN
NEW.VALOR_ICMS = 0;
IF (NEW.NO_IPI = 'N') THEN
NEW.VALOR_IPI_PRODUTOS = 0;
END
/* FECHOU A VENDA */
IF ((OLD.TIPO IN (2, 5)) AND (NEW.TIPO IN (3, 6))) THEN
BEGIN
/* USUÁRIO, DATA E HORA DO FECHAMENTO */
NEW.DATAFECHAMENTO = 'NOW';
NEW.FECHADO = 'S';
IF ((UDF_TRIM(NEW.USUARIOFECHAMENTO) = '') OR (NEW.USUARIOFECHAMENTO IS NULL)) THEN
NEW.USUARIOFECHAMENTO = USER;
/* VINCULAR SERIAL NA VENDA FECHADA */
IF (NEW.TIPO = 6) THEN
EXECUTE PROCEDURE VINCULO_SERIE NEW.CODIGO
RETURNING_VALUES NEW.SERIE;
/* TEMPOS DE ATENDIMENTO, SE FOR OS */
IF (NEW.TIPO = 3) THEN
BEGIN
SELECT
MIN(DATAINI),
MAX(DATAFIM),
SUM(THORAS)
FROM
OS_VENDER
WHERE
CODMOVIMENTO = NEW.CODIGO
INTO
:PRIMEIRO,
:ULTIMO,
:TOTAL;
NEW.TEMPO_CHEGADA    = CAST(UDF_MINUTESBETWEEN(NEW.DATA, PRIMEIRO) AS DOUBLE PRECISION) / 60;
NEW.TEMPO_CONCLUSAO  = CAST(UDF_MINUTESBETWEEN(NEW.DATA, ULTIMO) AS DOUBLE PRECISION) / 60;
NEW.TEMPO_LANCAMENTO = CAST(UDF_MINUTESBETWEEN(ULTIMO, NEW.DATAFECHAMENTO) AS DOUBLE PRECISION) / 60;
NEW.TEMPO_TECNICOS = TOTAL;
NEW.DATAINICIO  = PRIMEIRO;
NEW.DATATERMINO = ULTIMO;
IF (PRIMEIRO > NEW.DATAPREVISAO) THEN
NEW.TEMPO_FORADOPRAZO = CAST(UDF_MINUTESBETWEEN(NEW.DATAPREVISAO, PRIMEIRO) AS DOUBLE PRECISION) / 60;
ELSE
NEW.TEMPO_FORADOPRAZO = 0;
END
NEW.TEMPO_CICLO = CAST(UDF_MINUTESBETWEEN(NEW.DATA, NEW.DATAFECHAMENTO) AS DOUBLE PRECISION) / 60;
END
/* RATEIO DOS CUSTOS DE COMPRA */
IF ((NEW.ES = 1) AND (NEW.TIPO = 1)) THEN
EXECUTE PROCEDURE RATEIO_CUSTOS (
NEW.CODIGO,
NEW.VALOR_PRODUTOS,
NEW.VALOR_RATEIO_CUSTO);
END
^
SET TERM ; ^
INSERT INTO SCRIPTVER(NUMERO, DATA) VALUES (307, CURRENT_TIMESTAMP);
COMMIT;

/*
** ATIVAÇÃO - NOVOS CAMPOS II
*/
SET TERM ^ ;
ALTER TRIGGER STATUS_UPDATE
ACTIVE BEFORE UPDATE POSITION 0
AS
DECLARE VARIABLE PRIMEIRO TIMESTAMP;
DECLARE VARIABLE ULTIMO TIMESTAMP;
DECLARE VARIABLE TOTAL DOUBLE PRECISION;
DECLARE VARIABLE TDATAINI TIMESTAMP;
DECLARE VARIABLE TDATAFIM TIMESTAMP;
BEGIN
/* DATA DA ENTREGA */
IF ((NEW.QUEM_RECEBEU IS NOT NULL) AND (OLD.QUEM_RECEBEU IS NULL)) THEN
NEW.DATA_ENTREGA = CURRENT_TIMESTAMP;
/* DATA DA AUTORIZAÇÃO */
IF ((NEW.QUEM_AUTORIZOU IS NOT NULL) AND (OLD.QUEM_AUTORIZOU IS NULL)) THEN
NEW.DATA_AUTORIZADO = CURRENT_TIMESTAMP;
IF (NEW.TIPO = 1) THEN
BEGIN
IF (NEW.ES = 0) THEN
BEGIN
NEW.STATUS = 'ORC ABERTO';
IF (NEW.AUTORIZADO = 'S') THEN
BEGIN
NEW.STATUS = 'ORC AUTORIZADO';
END ELSE
BEGIN
IF (NEW.DATA_AUTORIZADO IS NOT NULL) THEN
NEW.STATUS = 'ORC NAO AUTO';
IF ((NEW.RETORNO = 'S') AND (NEW.DATA_RETORNO IS NOT NULL)) THEN
NEW.STATUS = 'ORC RETORNAR';
END
END
IF (NEW.ES = 1) THEN
BEGIN
if (NEW.FECHADO = 'S') then
NEW.STATUS = 'COMPRA';
ELSE
NEW.STATUS = 'PED COMPRA';
END
END
IF (NEW.TIPO = 2) THEN
BEGIN
NEW.STATUS = 'OS ABERTA';
/* VERIFICO SE O TECNICO JÁ INICIOU */
SELECT FIRST 1
DATAINI,
DATAFIM
FROM
OS_VENDER
WHERE
CODMOVIMENTO = NEW.CODIGO
INTO
:TDATAINI,
:TDATAFIM;
IF (TDATAINI IS NOT NULL) THEN
BEGIN
NEW.STATUS = 'OS INICIADA';
IF (NEW.DATA_AUTORIZADO IS NOT NULL) THEN
BEGIN
IF (NEW.AUTORIZADO = 'N') THEN
NEW.STATUS = 'OS NAO AUTO';
ELSE
IF (TDATAFIM IS NULL) THEN
NEW.STATUS = 'OS AUTORIZADA';
ELSE
NEW.STATUS = 'OS CONCLUIDA';
END
IF (NEW.DATA_ENTREGA IS NOT NULL) THEN
NEW.STATUS = 'OS ENTREGUE';
END
END
IF (NEW.TIPO = 3) THEN NEW.STATUS = 'OS FECHADA';
IF (NEW.TIPO = 4) THEN NEW.STATUS = 'PRE ASSITENCIA';
IF (NEW.TIPO = 5) THEN NEW.STATUS = 'VENDA ABERTA';
IF (NEW.TIPO = 6) THEN NEW.STATUS = 'VENDA FECHADA';
IF (NEW.TIPO = 7) THEN NEW.STATUS = 'N/F';
IF (NEW.TIPO = 8) THEN NEW.STATUS = 'REMESSA';
IF (NEW.TIPO = 9) THEN NEW.STATUS = 'ESTORNO';
/* VERIFICO O NUMERADOR */
EXECUTE PROCEDURE VALIDA_NUMERADOR(NEW.NUMERADOR, NEW.CODCLIENTE,
NEW.CODMARCA, NEW.CODMODELO, NEW.SERIE);
/* calcular impostos */
IF ((NEW.ES = 2)                  AND
(NEW.TIPO IN (3, 6))          AND
(OLD.TIPO IN (2, 5)))         THEN
BEGIN
/* TOTAL ICMS */
EXECUTE PROCEDURE CALC_ICMS(NEW.CODIGO)
RETURNING_VALUES NEW.VALOR_ICMS, NEW.VALOR_IPI_PRODUTOS;
IF (NEW.NO_ICMS = 'N') THEN
NEW.VALOR_ICMS = 0;
IF (NEW.NO_IPI = 'N') THEN
NEW.VALOR_IPI_PRODUTOS = 0;
END
/* FECHOU A VENDA */
IF ((OLD.TIPO IN (2, 5)) AND (NEW.TIPO IN (3, 6))) THEN
BEGIN
/* USUÁRIO, DATA E HORA DO FECHAMENTO */
NEW.DATAFECHAMENTO = 'NOW';
NEW.FECHADO = 'S';
IF ((UDF_TRIM(NEW.USUARIOFECHAMENTO) = '') OR (NEW.USUARIOFECHAMENTO IS NULL)) THEN
NEW.USUARIOFECHAMENTO = USER;
/* VINCULAR SERIAL NA VENDA FECHADA */
IF (NEW.TIPO = 6) THEN
EXECUTE PROCEDURE VINCULO_SERIE NEW.CODIGO
RETURNING_VALUES NEW.SERIE;
/* TEMPOS DE ATENDIMENTO, SE FOR OS */
IF (NEW.TIPO = 3) THEN
BEGIN
SELECT
MIN(DATAINI),
MAX(DATAFIM),
SUM(THORAS)
FROM
OS_VENDER
WHERE
CODMOVIMENTO = NEW.CODIGO
INTO
:PRIMEIRO,
:ULTIMO,
:TOTAL;
NEW.TEMPO_CHEGADA    = CAST(
UDF_MINUTESBETWEEN(NEW.DATA, PRIMEIRO)
AS DOUBLE PRECISION) / 60;
NEW.TEMPO_CONCLUSAO  = CAST(
UDF_MINUTESBETWEEN(NEW.DATA, ULTIMO)
AS DOUBLE PRECISION) / 60;
NEW.TEMPO_LANCAMENTO = CAST(
UDF_MINUTESBETWEEN(ULTIMO, NEW.DATAFECHAMENTO)
AS DOUBLE PRECISION) / 60;
NEW.TEMPO_TECNICOS = TOTAL;
NEW.DATAINICIO  = PRIMEIRO;
NEW.DATATERMINO = ULTIMO;
IF (PRIMEIRO > NEW.DATAPREVISAO) THEN
NEW.TEMPO_FORADOPRAZO = CAST(
UDF_MINUTESBETWEEN(NEW.DATAPREVISAO, PRIMEIRO)
AS DOUBLE PRECISION) / 60;
ELSE
NEW.TEMPO_FORADOPRAZO = 0;
END
NEW.TEMPO_CICLO = CAST(
UDF_MINUTESBETWEEN(NEW.DATA, NEW.DATAFECHAMENTO)
AS DOUBLE PRECISION) / 60;
END
/* RATEIO DOS CUSTOS DE COMPRA */
IF ((NEW.ES = 1) AND (NEW.TIPO = 1)) THEN
EXECUTE PROCEDURE RATEIO_CUSTOS (
NEW.CODIGO,
NEW.VALOR_PRODUTOS,
NEW.VALOR_RATEIO_CUSTO);
END
^
SET TERM ; ^
SET TERM ^ ;
ALTER PROCEDURE VENDA_FECHA (
CODMOVIMENTO INTEGER,
USUARIOFECHAMENTO VARCHAR(30),
OBSERVACOES VARCHAR(2048))
RETURNS (
RE_MOVIMENTO INTEGER)
AS
DECLARE VARIABLE TIPO INTEGER;
DECLARE VARIABLE ES INTEGER;
DECLARE VARIABLE FECHADO CHAR(1);
BEGIN
RE_MOVIMENTO = 0;
FECHADO = 'N';
/* LEIO O TIPO ATUAL */
SELECT
ES,
TIPO
FROM
MOVIMENTOS
WHERE
CODIGO = :CODMOVIMENTO
INTO
:ES,
:TIPO;
IF (TIPO IN (1, 2, 5)) THEN
BEGIN
IF ((ES = 2) AND (USUARIOFECHAMENTO > '')) THEN
BEGIN
IF (TIPO = 2) THEN
TIPO = 3;
ELSE
TIPO = 6;
FECHADO = 'S';
END
UPDATE
MOVIMENTOS
SET
TIPO = :TIPO,
USUARIOFECHAMENTO = :USUARIOFECHAMENTO,
DATAFECHAMENTO = CURRENT_TIMESTAMP,
OBSERVACOES = :OBSERVACOES,
FECHADO = :FECHADO
WHERE
CODIGO = :CODMOVIMENTO;
WHEN ANY DO RE_MOVIMENTO = 1;
END
END
^
SET TERM ; ^
INSERT INTO SCRIPTVER(NUMERO, DATA) VALUES (312, CURRENT_TIMESTAMP);
COMMIT;

/*
** AJUSTE NO ESTORNO DO ITEM
*/
SET TERM ^ ;
ALTER TRIGGER INDIVIDUAIS_BUESTOQUE
ACTIVE BEFORE UPDATE POSITION 1000
AS
BEGIN
/* APAGO OS RESTOS DO LINK COM A VENDA */
IF ((NEW.VENDIDO = 'N') AND (OLD.VENDIDO = 'S')) THEN
NEW.CODMOVSAIDA = NULL;
/* ESTATISTICA DE GIRO DE ESTOQUE */
IF ((NEW.VENDIDO = 'S') AND (OLD.VENDIDO = 'N')) THEN
BEGIN
/* DATA DA COMPRA */
SELECT
MO.DATA
FROM
MOVIMENTOS MO
WHERE
MO.CODIGO = NEW.CODMOVENTRADA
INTO
NEW.DATAENTRADA;
/* DATA DA VENDA */
SELECT
MO.DATA
FROM
MOVIMENTOS MO
WHERE
MO.CODIGO = NEW.CODMOVSAIDA
INTO
NEW.DATASAIDA;
/* TEMPO NO ESTOQUE */
NEW.TEMPOESTOQUE = CAST((NEW.DATASAIDA - NEW.DATAENTRADA) AS DOUBLE PRECISION);
END
END
^
SET TERM ; ^
UPDATE
INDIVIDUAIS
SET
CODMOVSAIDA = NULL
WHERE
VENDIDO = 'N';
INSERT INTO SCRIPTVER(NUMERO, DATA) VALUES (315, CURRENT_TIMESTAMP);
COMMIT;
